questionvariables.text:
/* Zufall */
kredit:rand_with_step(10000,20000,1000);
gebuehr:rand_with_step(500,1500,100);
monatszins:rand_with_step(.005,.015,.001);
lfzjahre:rand_with_step(3,6,1);
/* Berechnungen */
iPrint:monatszins*100;
lfzmonate:lfzjahre*12;
barwert:(1-(1+monatszins)^(-1*lfzmonate))/monatszins;
rate:decimalplaces(kredit/barwert,2);
eqn:(1+x)^(lfzmonate)-decimalplaces(((kredit-gebuehr)/rate),2)*x*(1+x)^(lfzmonate)-1=0;roots:allroots(eqn);roots2:sublist(roots,lambda([t],imagpart(rhs(t))=0))effmonat:ev(x,roots[2]);xstart:decimalplaces(effmonat+.005,3);
fxstart:decimalplaces(ev(lhs(eqn),x=xstart),5);
fabl:diff(lhs(eqn),x,1);
fablxstart:decimalplaces(ev(fabl,x=xstart),5);
ta1:decimalplaces(xstart-(fxstart/fablxstart),5);
fehler:abs(decimalplaces(ev(lhs(eqn),x=ta1),5));
ta2:fehler;
