/* Hilfsvariablen */
Farben:["rot","weiß"];
Urnen:["A","B","C"];
r:A[1]+B[1]+C[1];
w:A[2]+B[2]+C[2];
/* Wahrscheinlichkeit aus Urne A,B,C unter der Bedingung s bzw. w */
p:[[0,0,0],[0,0,0]];p[1]:[A[1]/r,B[1]/r,C[1]/r];
p[2]:[A[2]/w,B[2]/w,C[2]/w];
