/* Zufall */
rnd:rand([1,2]);
w:rand_with_step(5,10,1);
s:rand_with_step(4,10,1);
zw:rand_with_step(1,3,1);
zs:rand_with_step(1,3,1);

/* weitere Berechnungen */
typ:["wieder zurückgelegt","nicht wieder zurückgelegt"];
n:w+s;
z:zw+zs;

/* Fall: nicht wieder zurückgelegt */
tmp:1;
dum1: for a:1 thru zw step 1 do tmp:tmp*((w-a+1)/(n-a+1));
dum1: for a:1 thru zs step 1 do tmp:tmp*((s-a+1)/(n-zw-a+1));
tmp:decimalplaces(tmp,3);
/* Anpassung, falls Fall mit zurücklegen */
if rnd=1 then tmp:decimalplaces((w/n)^zw*(s/n)^zs,3);
ta1: tmp;

/* Zufallszahlen */
v1:rand_with_step(.93,.96,.001);
