/* Berechnung */
ew:v2*v3+v4*v5;
var:v2^2*v3+v4^2*v5-ew^2;
r1:ew+(sqrt(var)/20);

/* Ergebnis */
ta1:decimalplaces(r1,0);

/* Zufallsvariablen */
v1:rand_with_step(1.1,1.4,.01);
v2:rand_with_step(50000,200000,10000);
