egewinn:(v1-1)*v2*v3;
sgewinn:3*sqrt(v2^2*v3*(1-v3));
r1:egewinn-sgewinn;
egewinnk:egewinn*v4;
sgewinnk:3*sqrt(v4*v2^2*v3*(1-v3));
r2:egewinnk-sgewinnk;

/* Ergebnisse */
ta1:decimalplaces(r1,0);
ta2:decimalplaces(r2,0);

rnd1:rand_with_step(.5,.9,.01);
rnd2:rand_with_step(.1,.49,.01);
ta1:rnd1-rnd2;

ta:[["Ja",true],["Nein",false]];


/* The actual correct answer.    */
tac:["Ja"]
/* */
ta:random_permutation(ta);

